import React from 'react';
import './index.css';
import { Reply } from './Reply';
import { time } from 'console';
import { useState } from 'react';
interface ThreadsProps {
  userName: string;
  content: string;
  time: string;
  replyCount: number;
}

export const Threads: React.FC<ThreadsProps> = ({ userName, content,time,replyCount }) => {
    const [threads, setThreads] = useState(
        [
            {
                userName: "test",
                content: "test",
                time: "1時間前",
                replyCount: 2
            }
        ]
    );
    const [newThread, setNewThread] = useState({
        userName: "",
        content: "",
        time: "",
        replyCount: 0
    });
    const submitThread = () => {
        const newThreads = [...threads,newThread];
        setThreads(newThreads);
    }
    return (
        <div className="thread-container">
            <div className="thread">
                <div className="thread-header">
                    <div className="user-info">
                        <div className="thread-avatar"></div>
                        <span className="thread-username">{userName}</span>
                    </div>
                    <span className="thread-time">{time}</span>
                </div>
                <div className="thread-content">
                    <p>{content}</p>
                </div>
                <div className="thread-actions">
                    
                    <button className="action-button">返信数{replyCount}</button>
                </div>
            </div>
            
            
            <div className="thread-form">
                <input type="text" placeholder="ユーザー名" value={newThread.userName} onChange={(e) => setNewThread({...newThread, userName: e.target.value})} />
                <input type="text" placeholder="内容" value={newThread.content} onChange={(e) => setNewThread({...newThread, content: e.target.value})} />
                <button onClick={submitThread}>投稿</button>    
            </div>

            
        </div>
        
    )
}
